export default class ADTS {
    static isHeader(data: any, offset: any): boolean;
    static getFrameDuration(samplerate: any): number;
    static isHeaderPattern(data: any, offset: any): boolean;
    static getHeaderLength(data: any, offset: any): 7 | 9;
    static getFullFrameLength(data: any, offset: any): number;
    static parseFrameHeader(data: any, offset: any, pts: any, frameIndex: any, frameDuration: any): {
        headerLength: number;
        frameLength: number;
        stamp: any;
    };
    static appendFrame(sampleRate: any, data: any, offset: any, pts: any, frameIndex: any): {
        sample: {
            unit: any;
            pts: any;
            dts: any;
        };
        length: number;
    };
}
