export default class MediaInfo {
    static isBaseInfoReady(mediaInfo: any): boolean;
    static isVideoReady(mediaInfo: any): boolean;
    static isAudioReady(mediaInfo: any): boolean;
    mimeType: any;
    duration: any;
    /** @type {boolean} */
    hasVideo: boolean;
    /**
     * video media info
     * @type {VideoMediaInfo}
     */
    video: VideoMediaInfo;
    /** @type {boolean} */
    hasAudio: boolean;
    /**
     * video media info
     * @type {AudioMediaInfo}
     */
    audio: AudioMediaInfo;
    isComplete(): boolean;
}
export type VideoMediaInfo = {
    codec: string | null;
    width: number | null;
    height: number | null;
    profile: string | null;
    frameRate: {
        fixed: boolean;
        fps: number;
        fps_num: number;
        fps_den: number;
    };
    chromaFormat: string | null;
    parRatio: {
        width: number;
        height: number;
    };
};
export type AudioMediaInfo = {
    codec: string | null;
    sampleRate: number | null;
    sampleRateIndex: number | null;
    channelCount: number | null;
};
