export default M3U8Parser;
/**
 * Reference: https://tools.ietf.org/html/rfc8216#section-4.3
 */
declare class M3U8Parser {
    /**
     * @param {string} text
     * @param {string} baseurl
     * @return {{duration: number}}
     */
    static parse(text: string, baseurl?: string): {
        duration: number;
    };
    /**
     *
     * @param {string[]}refm
     * @param {string[]}refs
     * @param {*} ret
     * @param {string} baseurl
     * @param {boolean} discontinue
     * @param {number} nextId
     * @return {*}
     */
    static parseFrag(refm: string[], refs: string[], ret: any, baseurl: string, discontinue: boolean, nextId: number): any;
    /**
     * @param {string[]}refm
     * @param {*} ret
     */
    static parseDecrypt(refm: string[], ret: any): void;
    /**
     * @param {string} url
     * @return {boolean}
     */
    static isHTTPS(url: string): boolean;
}
